import { PrismaClient, Role } from "@prisma/client"
import bcrypt from "bcryptjs"

const prisma = new PrismaClient()

// Sample base64 encoded avatar images (small placeholder images)
const avatars = {
  superAdmin:
    "data:image/svg+xml;base64,",
  admin:
    "data:image/svg+xml;base64,",
  user1:
    "data:image/svg+xml;base64,",
  user2:
    "data:image/svg+xml;base64,",
  user3:
    "data:image/svg+xml;base64,",
}

async function main() {
  // Clear existing data
  await prisma.transaction.deleteMany()
  await prisma.user.deleteMany()

  // Create users with different roles and profile pictures
  const hashedPassword = await bcrypt.hash("password123", 10)

  const users = await Promise.all([
    // Super Admin
    prisma.user.create({
      data: {
        email: "superadmin@psp.com",
        name: "Super Admin",
        password: hashedPassword,
        role: Role.SUPER_ADMIN,
        balance: 10000,
        profilePicture: avatars.superAdmin,
      },
    }),
    // Admin
    prisma.user.create({
      data: {
        email: "admin@psp.com",
        name: "Admin User",
        password: hashedPassword,
        role: Role.ADMIN,
        balance: 5000,
        profilePicture: avatars.admin,
      },
    }),
    // Regular Users
    prisma.user.create({
      data: {
        email: "user1@example.com",
        name: "John Doe",
        password: hashedPassword,
        role: Role.USER,
        balance: 1500,
        profilePicture: avatars.user1,
      },
    }),
    prisma.user.create({
      data: {
        email: "user2@example.com",
        name: "Jane Smith",
        password: hashedPassword,
        role: Role.USER,
        balance: 2300,
        profilePicture: avatars.user2,
      },
    }),
    prisma.user.create({
      data: {
        email: "user3@example.com",
        name: "Bob Johnson",
        password: hashedPassword,
        role: Role.USER,
        balance: 800,
        isActive: false,
        profilePicture: avatars.user3,
      },
    }),
  ])

  // Create sample transactions
  await Promise.all([
    prisma.transaction.create({
      data: {
        amount: 500,
        description: "Payment for services",
        status: "COMPLETED",
        type: "PAYMENT",
        senderId: users[2].id,
        receiverId: users[3].id,
      },
    }),
    prisma.transaction.create({
      data: {
        amount: 1000,
        description: "Wallet deposit",
        status: "COMPLETED",
        type: "DEPOSIT",
        receiverId: users[2].id,
      },
    }),
    prisma.transaction.create({
      data: {
        amount: 250,
        description: "Online purchase",
        status: "COMPLETED",
        type: "PAYMENT",
        senderId: users[3].id,
        receiverId: users[2].id,
      },
    }),
  ])

  console.log("Database seeded successfully!")
}

main()
  .catch((e) => {
    console.error(e)
    process.exit(1)
  })
  .finally(async () => {
    await prisma.$disconnect()
  })
